
App.ProjectModel = Backbone.Model.extend({
  "defaults": {
    "name": "Untitled",
    "description": ""
  },
  nameURI: function() {
    var name, uri;
    name = this.get("name");
    name = name.replace(/ /g, "_");
    this.set("uri", name);
    uri = this.get("uri");
    console.log("uri created as", uri);
    return uri;
  },
  initialize: function() {
    if (this.get("name")) {
      return this.nameURI();
    }
  }
});

